
-- SQLite schema for AWS Startup Tycoon choices DB
PRAGMA journal_mode = WAL;
PRAGMA foreign_keys = ON;

DROP TABLE IF EXISTS choices;
DROP TABLE IF EXISTS turns;

CREATE TABLE turns (
  turn INTEGER PRIMARY KEY,
  event TEXT NOT NULL
);

CREATE TABLE choices (
  id INTEGER PRIMARY KEY,
  turn INTEGER NOT NULL,
  text TEXT NOT NULL,
  users INTEGER NOT NULL,
  cash INTEGER NOT NULL,
  trust INTEGER NOT NULL,
  infra_json TEXT NOT NULL,
  next_turn INTEGER NOT NULL,
  FOREIGN KEY(turn) REFERENCES turns(turn)
);

CREATE INDEX idx_choices_turn ON choices(turn);
CREATE INDEX idx_choices_next_turn ON choices(next_turn);

